int isValidParentheses(char* s) {
    int len = strlen(s);
    char stack[100];
    int top = -1;

    for (int i = 0; i < len; i++) {
        char c = s[i];
        if (c == '(' || c == '[' || c == '{') {
            stack[++top] = c;
        } else {
            if (top == -1) return 0;

            char topChar = stack[top--];
            if ((c == ')' && topChar != '(') ||
                (c == ']' && topChar != '[') ||
                (c == '}' && topChar != '{')) {
                return 0;
            }
        }
    }

    return top == -1 ? 1 : 0;
}
